mapper.select:
		SELECT ,
		FROM job_info AS t
		INNER JOIN job_project p on t.project_id=p.id
		INNER JOIN job_user u on t.user_id = u.id


				AND t.job_group = #{jobGroup}


                AND t.trigger_status = #{triggerStatus}


				AND t.job_desc like CONCAT(CONCAT('%', #{jobDesc}), '%')


				AND t.glue_type like CONCAT(CONCAT('%', #{glueType}), '%')


				AND t.user_id = #{userId}


                AND t.project_id IN

                    #{projectId}



		ORDER BY id DESC
		LIMIT #{offset}, #{pagesize}

		SELECT count(1)
		FROM job_info AS t
		INNER JOIN job_project p on t.project_id=p.id


				AND t.job_group = #{jobGroup}


                AND t.trigger_status = #{triggerStatus}


				AND t.job_desc like CONCAT(CONCAT('%', #{jobDesc}), '%')


				AND t.glue_type like CONCAT(CONCAT('%', #{glueType}), '%')


				AND t.user_id =#{userId}


                AND t.project_id IN

                    #{projectId}

SELECT 
FROM job_info AS t
WHERE t.id = #{id}

SELECT 
FROM job_info AS t
WHERE t.job_group = #{jobGroup}

SELECT 
FROM job_info AS t
WHERE t.trigger_status = 1
	and t.trigger_next_time  <=  #{maxNextTime}
ORDER BY id ASC
LIMIT #{pagesize}

mapper.update:
UPDATE job_info
SET
	trigger_last_time = #{triggerLastTime},
	trigger_next_time = #{triggerNextTime},
	trigger_status = #{triggerStatus}
WHERE id = #{id}

UPDATE job_info
SET
	inc_start_time = #{incStartTime}
WHERE id = #{id}

UPDATE job_info
SET
	last_handle_code = #{lastHandleCode}
WHERE id = #{id}

UPDATE job_info
SET
inc_start_id = #{incStartId}
WHERE id = #{id}
