mapper.delete:
DELETE FROM job_registry
WHERE id in

    #{item}

mapper.update:
UPDATE job_registry
SET `update_time` = #{updateTime},
	`cpu_usage`=#{cpuUsage},
	`memory_usage`=#{memoryUsage},
	`load_average`=#{loadAverage}
WHERE `registry_group` = #{registryGroup}
  AND `registry_key` = #{registryKey}
  AND `registry_value` = #{registryValue}

mapper.delete:
DELETE FROM job_registry
WHERE registry_group = #{registryGroup}
	AND registry_key = #{registryKey}
	AND registry_value = #{registryValue}
